mapper.select:
SELECT id, image_url, link_url, sort, status, create_time, update_time, create_by, update_by, remark FROM t_banner
AND (image_url LIKE CONCAT('%', #{search}, '%') OR link_url LIKE CONCAT('%', #{search}, '%'))
ORDER BY sort ASC, create_time DESC LIMIT #{offset}, #{size}

SELECT id, image_url, link_url, sort, status, create_time, update_time, create_by, update_by, remark
FROM t_banner
WHERE id = #{id}

mapper.update:
UPDATE t_banner
image_url = #{imageUrl}, link_url = #{linkUrl}, sort = #{sort}, status = #{status}, update_time = #{updateTime}, update_by = #{updateBy}, remark = #{remark},
WHERE id = #{id}
